+/* Phase 1 - Step 1 - Global Font + Reset */
+@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap");
+body {
+  font-family: 'Poppins', sans-serif;
+}
+
+* {
+  margin: 0;
+  padding: 0;
+  box-sizing: border-box;
+  font-family: "Poppins", sans-serif;
+}
+
+/* Links and text default */
+a, p, span, h1, h2, h3, h4, h5, h6 {
+  color: #333;
+}
+
+/* Dark Mode Link */
+.dark-mode a, .dark-mode p, .dark-mode span, .dark-mode h1, .dark-mode h2, .dark-mode h3, .dark-mode h4, .dark-mode h5, .dark-mode h6 {
+  color: #f1f1f1;
+}
+
+
+
+/* Phase 1 - Step 4 - Navbar Polishing */
+.navbar {
+  background: linear-gradient(to right, #00b09b, #96c93d);
+  /* padding: 12px 20px; */
+  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
+}
+
+.navbar-brand {
+  font-family: 'Poppins', sans-serif;
+  font-weight: bold;
+  font-size: 24px;
+  color: #ffffff;
+}
+
+.navbar .nav-link {
+  color: #ffffff;
+  /* margin: 0 12px; */
+  font-weight: 500;
+  transition: color 0.3s;
+}
+
+.navbar .nav-link:hover {
+  color: #ffefba;
+}
+
+/* Dropdown Menu */
+.dropdown-menu {
+  background-color: #ffffff;
+  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
+}
+
+.dropdown-item:hover {
+  background-color: #96c93d;
+  color: #fff;
+}
+
+/* Phase 1 - Step 4 - Footer Polishing */
+.footer {
+  background: linear-gradient(to right, #434343, #000000);
+  color: #f8f9fa;
+  padding: 30px 20px;
+  text-align: center;
+}
+
+.footer a {
+  color: #ffefba;
+  text-decoration: none;
+  margin: 0 10px;
+  font-weight: 500;
+}
+
+.footer a:hover {
+  color: #ffffff;
+  border-bottom: 1px solid #ffefba;
+}
+
+
+/* step 2 */
+/* Phase 1 - Step 2 - Product Card Styling */
+
+/* .card { */
+  /* width: 18rem; */
+  /* border-radius: 10px; */
+  /* overflow: hidden; */
+  /* box-shadow: 0 4px 8px rgba(0,0,0,0.08); */
+  /* transition: transform 0.3s ease, box-shadow 0.3s ease; */
+  /* background: #fff; */
+  /* color:#000; */
+/* } */
