/* DARK MODE */
.dark-mode {
  background-color: #121212;
  color: #f1f1f1;
}

.dark-mode .card {
  background-color: #1e1e1e;
  color: #fff;
  box-shadow: 0 0 15px rgba(255, 255, 255, 0.1);
}

.dark-mode .navbar,
.dark-mode .footer {
  background: #1f1f1f;
  color: #fff;
}

.dark-mode .btn {
  background-color: #333;
  color: white;
  border: 1px solid #555;
}

.dark-mode .btn:hover {
  background-color: #555;
}
